package webDriverPackage;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//print 1 to 10 even number
		//2, 4, 6, 8, 10
		int i;
		for ( i=2; i <10; i= i+2)
			System.out.println(i);
			
		//Validation: i=i+2;
		//iterarion1: 2+2= 4
		//iterarion2: 4+2= 6
		//iterarion3: 6+2= 8
		//iterarion4: 8+2= 10
		//iterarion5: 10+2= 12
		//0r
		for ( i=2; i <=10; i= i+2)
			System.out.println(i);
	}

}
